!(function (e) {
    "use strict";
    e(window).on("load", function () {
        e(".preloader").fadeOut(1e3);
        var i = e(".grid-memberlist").isotope({
                itemSelector: ".filter-item",
                layoutMode: "fitRows",
            }),
            s = {
                ium: function () {
                    return e(this).find(".name").text().match(/ium$/);
                },
            };
        e(".button-group").on("click", ".filter-btn", function () {
            var l = e(this).attr("data-filter");
            (l = s[l] || l), i.isotope({ filter: l });
        }),
            e(".button-group").each(function (i, s) {
                var l = e(s);
                l.on("click", ".filter-btn", function () {
                    l.find(".is-checked").removeClass("is-checked"),
                        e(this).addClass("is-checked");
                });
            });
    }),
        e(document).ready(function () {
            e("ul>li>.submenu").parent("li").addClass("menu-item-has-children"),
                e("ul")
                    .parent("li")
                    .on("hover", function () {
                        var i = e(this).find("ul");
                        if (
                            e(i).offset().left + i.width() >
                            e(window).width()
                        ) {
                            var s = -e(i).width();
                            i.css({ left: s });
                        }
                    }),
                e(".menu li a").on("click", function (i) {
                    var s = e(this).parent("li");
                    s.hasClass("open")
                        ? (s.removeClass("open"),
                          s.find("li").removeClass("open"),
                          s.find("ul").slideUp(300, "swing"))
                        : (s.addClass("open"),
                          s.children("ul").slideDown(300, "swing"),
                          s.siblings("li").children("ul").slideUp(300, "swing"),
                          s.siblings("li").removeClass("open"),
                          s.siblings("li").find("li").removeClass("open"),
                          s.siblings("li").find("ul").slideUp(300, "swing"));
                }),
                e(".ellepsis-bar").on("click", function (i) {
                    var s = e(".header-top");
                    s.hasClass("open")
                        ? (s.removeClass("open"),
                          s.slideUp(300, "swing"),
                          e(".overlayTwo").removeClass("active"))
                        : (s.addClass("open"),
                          s.slideDown(300, "swing"),
                          e(".overlayTwo").addClass("active"));
                }),
                e(".header-bar").on("click", function () {
                    e(this).toggleClass("active"),
                        e(".menu").toggleClass("active");
                });
            var i = e("header");
            e(window).on("scroll", function () {
                e(this).scrollTop() > 300
                    ? i.addClass("header-fixed fadeInUp")
                    : i.removeClass("header-fixed fadeInUp");
            }),
                e(function () {
                    e(window).on("scroll", function () {
                        e(this).scrollTop() > 300
                            ? e(".scrollToTop").css({
                                  bottom: "5%",
                                  opacity: "1",
                                  transition: "all .5s ease",
                              })
                            : e(".scrollToTop").css({
                                  bottom: "-30%",
                                  opacity: "0",
                                  transition: "all .5s ease",
                              });
                    }),
                        e("a.scrollToTop").on("click", function () {
                            return (
                                e("html, body").animate({ scrollTop: 0 }, 500),
                                !1
                            );
                        });
                }),
                e(".price-item").on("mouseenter", function () {
                    e(this)
                        .addClass("active")
                        .parent()
                        .siblings()
                        .find(".price-item")
                        .removeClass("active");
                });
        });
})(jQuery);
